import React, { useState } from 'react';
import styled from 'styled-components';

const InputWrapper = styled.div`
  position: relative;
  width: 100%;
`;

const StyledInput = styled.input`
  width: 100%;
  padding: 12px 16px;
  border: 1px solid #d1d5db;
  border-radius: 8px;
  font-family: 'Inter', sans-serif;
  font-size: 14px;
  line-height: 1.5;
  color: #111827;
  background-color: #ffffff;
  transition: all 0.2s ease;

  &::placeholder {
    color: #9ca3af;
  }

  &:focus {
    outline: none;
    border-color: #2563eb;
    box-shadow: 0 0 0 3px rgba(37, 99, 235, 0.1);
  }

  &:disabled {
    background-color: #f9fafb;
    color: #6b7280;
    cursor: not-allowed;
  }

  /* Size variants */
  ${props => {
    switch (props.size) {
      case 'small':
        return `
          padding: 8px 12px;
          font-size: 13px;
        `;
      case 'large':
        return `
          padding: 16px 20px;
          font-size: 16px;
        `;
      default:
        return '';
    }
  }}

  /* State styles */
  ${props => {
    if (props.error) {
      return `
        border-color: #dc2626;

        &:focus {
          border-color: #dc2626;
          box-shadow: 0 0 0 3px rgba(220, 38, 38, 0.1);
        }
      `;
    }
    if (props.success) {
      return `
        border-color: #059669;

        &:focus {
          border-color: #059669;
          box-shadow: 0 0 0 3px rgba(5, 150, 105, 0.1);
        }
      `;
    }
    return '';
  }}
`;

const Label = styled.label`
  display: block;
  font-size: 14px;
  font-weight: 500;
  color: #374151;
  margin-bottom: 6px;

  ${props => props.required && `
    &::after {
      content: ' *';
      color: #dc2626;
    }
  `}
`;

const HelperText = styled.p`
  margin: 4px 0 0 0;
  font-size: 12px;
  color: #6b7280;

  ${props => props.error && `
    color: #dc2626;
  `}

  ${props => props.success && `
    color: #059669;
  `}
`;

const InputIcon = styled.span`
  position: absolute;
  right: 12px;
  top: 50%;
  transform: translateY(-50%);
  color: #9ca3af;
  pointer-events: none;
`;

const Input = ({
  label,
  placeholder,
  value,
  onChange,
  type = 'text',
  size = 'medium',
  disabled = false,
  required = false,
  error = false,
  success = false,
  helperText,
  icon,
  ...props
}) => {
  const [inputValue, setInputValue] = useState(value || '');

  const handleChange = (e) => {
    const newValue = e.target.value;
    setInputValue(newValue);
    if (onChange) {
      onChange(e);
    }
  };

  return (
    <InputWrapper>
      {label && (
        <Label required={required}>
          {label}
        </Label>
      )}
      <StyledInput
        type={type}
        placeholder={placeholder}
        value={inputValue}
        onChange={handleChange}
        size={size}
        disabled={disabled}
        error={error}
        success={success}
        {...props}
      />
      {icon && <InputIcon>{icon}</InputIcon>}
      {helperText && (
        <HelperText error={error} success={success}>
          {helperText}
        </HelperText>
      )}
    </InputWrapper>
  );
};

export default Input;
