import React from 'react';
import styled from 'styled-components';

const StyledButton = styled.button`
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 8px 16px;
  font-size: 14px;
  font-weight: 500;
  border-radius: 6px;
  cursor: pointer;
  transition: all 0.2s ease;
  font-family: inherit;

  /* Primary variant */
  ${props => props.variant === 'primary' && `
    background: ${props.theme.colors.buttonPrimary};
    color: ${props.theme.colors.buttonText};
    border: none;

    &:hover {
      background: ${props.theme.colors.buttonHover};
    }

    &:disabled {
      background: ${props.theme.colors.buttonPrimary}80;
      cursor: not-allowed;
    }
  `}

  /* Secondary variant */
  ${props => props.variant === 'secondary' && `
    background: transparent;
    color: ${props.theme.colors.buttonPrimary};
    border: 1px solid ${props.theme.colors.buttonPrimary};

    &:hover {
      background: ${props.theme.colors.activeBackground};
    }

    &:disabled {
      color: ${props.theme.colors.textSecondary};
      border-color: ${props.theme.colors.border};
      cursor: not-allowed;
    }
  `}

  /* Text variant */
  ${props => props.variant === 'text' && `
    background: transparent;
    color: ${props.theme.colors.buttonPrimary};
    border: none;
    padding: 8px;

    &:hover {
      background: ${props.theme.colors.activeBackground};
    }

    &:disabled {
      color: ${props.theme.colors.textSecondary};
      cursor: not-allowed;
    }
  `}

  /* Size variants */
  ${props => props.size === 'small' && `
    padding: 6px 12px;
    font-size: 12px;
  `}

  ${props => props.size === 'large' && `
    padding: 12px 24px;
    font-size: 16px;
  `}

  /* Full width variant */
  ${props => props.fullWidth && `
    width: 100%;
  `}
`;

const Button = ({ 
  children, 
  variant = 'primary', 
  size = 'medium',
  fullWidth = false,
  ...props 
}) => {
  return (
    <StyledButton
      variant={variant}
      size={size}
      fullWidth={fullWidth}
      {...props}
    >
      {children}
    </StyledButton>
  );
};

export default Button;
