import React, { useState } from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import styled from 'styled-components';

const NavContainer = styled.nav`
  width: 250px;
  position: fixed;
  left: 0;
  top: 0;
  height: 100%;
  background: #ffffff;
  border-right: 1px solid #E2E8F0;
  font-size: 14px;
  font-family: 'Segoe UI', system-ui, sans-serif;
  -webkit-transition: all 0.5s ease;
  -moz-transition: all 0.5s ease;
  -o-transition: all 0.5s ease;
  transition: all 0.5s ease;
  z-index: 1000;
  overflow-y: auto;

  @media (max-width: 768px) {
    position: fixed;
    left: ${props => props.isOpen ? '0' : '-250px'};
    z-index: 1000;
    transition: left 0.3s ease;
    box-shadow: ${props => props.isOpen ? '2px 0 8px rgba(0,0,0,0.1)' : 'none'};
  }
`;

const SidebarHeader = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  padding: 16px 16px 16px 16px;
  background: #fff;
  border-bottom: 1px solid #E2E8F0;
`;

const HeaderLink = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  text-decoration: none;
  width: 100%;
`;

const CakeLogo = styled.div`
  width: 32px;
  height: 32px;
  margin-right: 12px;
  background: #1D4ED8;
  border-radius: 6px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-weight: bold;
  font-size: 16px;
`;

const CakeTitleGroup = styled.div`
  display: flex;
  flex-direction: column;
`;

const CakeTitle = styled.span`
  font-size: 16px;
  font-weight: 700;
  color: #171717;
  line-height: 1.2;
`;

const CakeVersion = styled.span`
  font-size: 10px;
  color: #64748B;
  margin-top: 2px;
`;

const SidebarNav = styled.div`
  width: 250px;
  margin: 0;
  padding: 16px 0 0 0;
`;

const NavList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
`;

const NavItem = styled.li`
  width: 100%;
`;

const NavLinkStyled = styled(NavLink)`
  width: calc(100% - 32px);
  margin: 0 16px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 0 0 20px;
  height: 40px;
  text-decoration: none;
  color: #171717;
  position: relative;
  font-weight: 400;
  font-size: 0.9rem;
  line-height: 40px;
  border-radius: 4px;
  margin-top: 1px;
  margin-bottom: 1px;
  transition: background 0.2s, color 0.2s;
  text-align: left;
  background: none;
  border: none;
  cursor: pointer;

  &:hover:not(.active) {
    color: #171717;
    background-color: #F1F5F9;
  }

  &.active {
    background-color: #EFF6FF;
    color: #1D4ED8;
    font-weight: 600;
  }

  &.active::before {
    content: '';
    position: absolute;
    left: 0;
    top: 0;
    bottom: 0;
    width: 4px;
    background: #1D4ED8;
    border-radius: 4px 0 0 4px;
  }
`;

const SubmenuToggle = styled.button`
  width: calc(100% - 32px);
  margin: 0 16px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 0 0 20px;
  height: 40px;
  text-decoration: none;
  color: #171717;
  position: relative;
  font-weight: 700;
  font-size: 0.75rem;
  line-height: 40px;
  border-radius: 4px;
  margin-top: 8px;
  margin-bottom: 4px;
  transition: background 0.2s, color 0.2s;
  text-align: left;
  background: none;
  border: none;
  cursor: pointer;
  letter-spacing: 0.08em;
  text-transform: uppercase;

  &:hover {
    color: #171717;
    background: none;
  }
`;

const Chevron = styled.span`
  transition: transform 0.3s ease;
  margin-right: 0;
  color: #171717;

  ${props => props.expanded && `
    transform: rotate(180deg);
  `}
`;

const Submenu = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
  max-height: ${props => props.expanded ? '1000px' : '0'};
  overflow: hidden;
  transition: max-height 0.3s ease-out;
`;

const SubmenuItem = styled.li`
  width: 100%;
`;

const SubmenuLink = styled(NavLink)`
  display: block;
  padding: 4px 16px 4px 20px;
  font-weight: 400;
  font-size: 0.9rem;
  line-height: 16px;
  color: #171717;
  text-decoration: none;
  transition: background 0.2s, color 0.2s;
  position: relative;

  &:hover {
    background-color: #F1F5F9;
    color: #171717;
  }

  &.active {
